import { Context } from "context";

/******************************/

export default function bindLocalStorageGlobalStore(
    context: Context
) {
    const globalStore = context.stores.global;

    let unsubscriber = globalStore.subscribe(() => {
        window.localStorage.setItem(
            'global',
            JSON.stringify(globalStore.getState())
        );
    });

    globalStore.actions.importDb();
}