import { EventEmitter } from "events";
import Item from 'core/data/objects/Item';

/******************************/

export default class Events extends EventEmitter {
    emit(command: 'hover/map', data: {x: number, y: number}): boolean;
    emit(command: 'hover/item', item: Item): boolean;
    emit(command: 'map/goTo', data: {x: number, y: number}): boolean;
    emit(command: 'map/move', value: boolean): boolean;
    emit(command: 'map/enter'): boolean;
    emit(command: 'map/leave'): boolean;

    emit(command: string, data?: any): boolean {
        return super.emit(command, data);
    }

    /******************************/

    on(command: 'hover/map', listener: (data: {
        x: number,
        y: number
    }) => void): this;
    on(command: 'hover/item', listener: (item: Item) => void): this;
    on(command: 'map/goTo', listener: (data: {
        x: number,
        y: number
    }) => void): this;
    on(command: 'map/move', listener: (value: boolean) => void): this;
    on(command: 'map/enter', listener: () => void): this;
    on(command: 'map/leave', listener: () => void): this;

    on(command: string, listener: (data: any) => void): this {
        return super.on(command, listener);
    }
}