import Events from "core/Events";
import MapData from "core/data/MapData";
import Village from "core/data/objects/Village";

/******************************/

export default function featureClickMap(
    events: Events,
    mapData: MapData
): void {
    events.on('click/map', ({x, y}: {x: number, y: number}) => {
        let village: Village = mapData.getVillageByCoords(x, y);

        events.emit('click/item', village);
    });
}