import Village from "./Village";

/******************************/

export default abstract class Item {
    id: number = null;
    mapItemType: string = null;
    name: string = null;
    points: number = 0;
    group: Item = null;

    growth: number = 0;
    activity: number = 0;

    /******************************/

    setGrowth(time: number) {
        if (time > this.growth) {
            this.growth = time;
        }
    }

    setActivity(time: number) {
        if (time > this.activity) {
            this.activity = time;
        }
    }

    abstract getVillages(): Village[];
}
