export function formatNumber(
    number: number
): string {
    let arr: string[],
        nr: string;

    arr = [];
    nr = number.toString()
    if (nr.length % 3) {
        arr.push(nr.substr(0, nr.length % 3));
        nr = nr.substr(nr.length % 3, nr.length);
    }
    while (nr) {
        arr.push(nr.substr(0, 3));
        nr = nr.substr(3, nr.length);
    }
    return arr.join('Â ');
}
