import * as React from 'react';

export function devFormatNumber(
    number: number
): string[] {
    let arr: string[],
        nr: string;

    arr = [];
    nr = number.toString()
    if (nr.length % 3) {
        arr.push(nr.substr(0, nr.length % 3));
        nr = nr.substr(nr.length % 3, nr.length);
    }
    while (nr) {
        arr.push(nr.substr(0, 3));
        nr = nr.substr(3, nr.length);
    }

    return arr;
}

export function formatNumber(
    number: number
): string {
    return devFormatNumber(number).join('Â ');
}

export function formatNumberReact(
    value: number
): JSX.Element[] {
    let values = devFormatNumber(value),
        result = [];

    for (let i = 0, l = values.length; i < l; i++) {
        result.push(
            React.createElement('span', {
                key: l - i,
                className: `value-${ l - i }`,
                children: [
                    values[ i ]
                ]
            })
        );
    }

    return result;
}