import Item from 'map/data/MapData/Item';
import Tribe from 'map/data/MapData/Tribe';
import Character from 'map/data/MapData/Character';

/******************************/

export default class Village extends Item {
    x: number = null;
    y: number = null;

    provinceId: number = null;
    character: Character = null;
    characterId: number = null;
    tribe: Tribe = null;

    growth: number = null;
    activity: number = null;
    diffPoints: number = null;
    conquer: number = null;
    previousCharacter: Character = null;

    /******************************/

    setCharacter(character: Character) {
        this.character = character;
        this.group = character;
        this.tribe = this.character.tribe;
        this.character.addVillage(this);
    }

    getVillages(): Village[] {
        return [this];
    }
}
