import Package from "stores/@/markers/Package";
import BaseState from "stores/@/BaseState";

/******************************/

export default class Markers extends BaseState {
    active = 'default';
    packages = {};

    constructor() {
        super();
        this.packages['default'] = new Package('default', 'Default');
    }

    /******************************/

    getActivePackage(): Package {
        return this.packages[ this.active ];
    }
}