export const actions = {
    importDb(): void {
        this.dispatch({
            type: 'command/import'
        });
    },
    setDontSpy(value: boolean): void {
        this.dispatch({
            type: 'dontSpy',
            prefix: 'analytics',
            value: value
        });
    },
    setGdrpShown(value: boolean): void {
        this.dispatch({
            type: 'gdrpShown',
            prefix: 'analytics',
            value: value
        });
    },
    setMapColor(
        key: string,
        value: string
    ): void {
        this.dispatch({
            type: key,
            prefix: 'map',
            value: value
        });
    }
}
