import * as types from "./types";

/******************************/

export const actions = {
    importDb(value: any): void {
        this.dispatch({type: types.IMPORT_DB, value: value});
    },
    setDontSpy(value: boolean): void {
        this.dispatch({type: 'dontSpy', prefix: 'analytics', value: value});
    },
    setGdrpShown(value: boolean): void {
        this.dispatch({type: 'gdrpShown', prefix: 'analytics', value: value});
    },
    setMapColor(
        key: string,
        value: string
    ): void {
        this.dispatch({
            type: key,
            prefix: 'map',
            value: value
        });
    }
}
