import * as $mapColors from 'stores/@/mapColors';

/******************************/

export const actions = {
    importDb(): void {
        this.dispatch({
            type: 'command/import'
        });
    },
    setDontSpy(value: boolean): void {
        this.dispatch({
            type: 'dontSpy',
            prefix: 'analytics',
            value: value
        });
    },
    setGdrpShown(value: boolean): void {
        this.dispatch({
            type: 'gdrpShown',
            prefix: 'analytics',
            value: value
        });
    },
    ...$mapColors.createActions()
}
