import * as types from "./types";
import { Actions } from "./types";

/******************************/

export const actions: Actions = {
    importDb(value: any): void {
        this.dispatch({type: types.IMPORT_DB, value: value});
    },
    setDontSpy(value: boolean): void {
        this.dispatch({type: types.SET_DONT_SPY, value: value});
    },
    setGdrpShown(value: boolean): void {
        this.dispatch({type: types.SET_GDRP_SHOWN, value: value});
    }
}
