import { createStore, State } from './';
import Store from 'stores/StoreWrapper';

/******************************/

describe('actions', () => {
    let store: Store<State>;

    beforeAll(() => {
        store = createStore();
    });

    it('dont spy', () => {
        [false, true].map((value) => {
            store.actions.setDontSpy(value);

            expect(store.getState()['dontSpy']).toBe(value);
        });
    });
});