import StoreWrapper from 'stores/StoreWrapper';

import { reducer } from './reducer';
import State from './state';
import { actions } from './actions';

/******************************/

type Actions = typeof actions;

/******************************/

export default function createGlobalStore() {
    return new StoreWrapper<State, Actions>(reducer, actions, 'global');
};
export type GlobalStore = ReturnType<typeof createGlobalStore>;
export { createGlobalStore as createStore, State, Actions };
