import { Stores } from "stores";
import MapColors from "stores/@/MapColors";

/******************************/

export default function createHelper(stores: Stores) {
    return {
        getMapColors(): MapColors {
            let mapColors = new MapColors(),
                globalMapColors = stores.global.getState().mapColors,
                worldMapColors = stores.world.getState().mapColors;

            for (let key in mapColors) {
                mapColors[ key ] = worldMapColors[ key ].active && worldMapColors[ key ] || globalMapColors[ key ];
            }

            return mapColors;
        }
    };
}