import * as globalStore from 'stores/global';
import * as worldStore from 'stores/world';
import * as stateStore from 'stores/state';
import createHelper from 'stores/helper';

/******************************/

function createStoreMap() {
    return {
        global: globalStore.createStore(),
        world: worldStore.createStore(),
        state: stateStore.createStore()
    };
}
export type Stores = ReturnType<typeof createStoreMap>;

export default function createStores() {
    const map_stores = createStoreMap();

    return {
        ...map_stores,
        helper: createHelper(map_stores)
    };
}
export type StoreBox = ReturnType<typeof createStores>;
export type StoreHelper = ReturnType<typeof createHelper>;
