import { INIT_TILE_SIZE } from "appConstants";

/******************************/

export type StateView = {
    size: number,
    x: number,
    y: number
};

export const initStateView = {
    x: 500,
    y: 500,
    size: INIT_TILE_SIZE
};

export function createReducers<State extends {view: StateView}>() {
    return {
        'view/set': (state: State, value: StateView) => {
            const nextState = { ...state };

            nextState.view = value;

            return nextState;
        }
    };
};

export function createActions<Store>(this: Store) {
    return {
        setView(value: {
            x: number,
            y: number,
            size: number
        }): void {
            this.dispatch({
                type: 'view/set',
                value
            });
        }
    };
};
