export const actions = {
    importDb(
        worldKey: string
    ): void {
        this.dispatch({
            type: 'command/import',
            worldKey
        });
    },
    setView(data: {
        x: number,
        y: number,
        size: number
    }): void {
        this.dispatch({
            type: 'view',
            value: data
        });
    }
}
