import * as mapColors from 'stores/@/MapColors';

/******************************/

export const actions = {
    importDb(
        marketId: string,
        worldId: string
    ): void {
        this.dispatch({
            type: 'command/import',
            marketId, worldId
        });
    },
    setWorld(
        marketId: string,
        worldId: string
    ): void {
        this.dispatch({
            type: 'command/import',
            marketId, worldId
        });
    },
    setView(data: {
        x: number,
        y: number,
        size: number
    }): void {
        this.dispatch({
            type: 'view',
            value: data
        });
    },
    setMapColor: mapColors.action
}
