import * as $view from 'stores/world/@/view';
import * as $mapColors from 'stores/world/@/mapColors';
import * as $markers from 'stores/world/@/markers';

/******************************/

export const actions = {
    importDb(
        marketId: string,
        worldId: string
    ): void {
        this.dispatch({
            type: 'command/import',
            marketId, worldId
        });
    },
    setWorld(
        marketId: string,
        worldId: string
    ): void {
        this.dispatch({
            type: 'command/import',
            marketId, worldId
        });
    },
    ...$view.createActions(),
    ...$mapColors.createActions(),
    ...$markers.createActions()
}
