import StoreWrapper from 'stores/StoreWrapper';

import { reducer } from './reducer';
import State from './state';
import { actions } from './actions';

/******************************/

type Actions = typeof actions;

/******************************/

export default function createWorldStore() {
    return new StoreWrapper<State, Actions>(reducer, actions);
};
export type WorldStore = ReturnType<typeof createWorldStore>;
export { createWorldStore as createStore, State, Actions };
