import View from "stores/@/View";
import MapColors from "stores/@/MapColors";
import BaseState from "stores/@/BaseState";
import Markers from "stores/@/Markers";

/******************************/

export default class State extends BaseState {
    worldKey = null;
    view: View;
    mapColors: MapColors;
    markers: Markers;

    /******************************/

    init() {
        this.view = new View();
        this.mapColors = new MapColors();
        this.markers = new Markers();

        for (let key in this.mapColors) {
            this.mapColors[ key ] = null;
        }

        return this;
    }
}