import * as React from 'react';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { ViewContext } from 'view/viewContext';

/******************************/

type ComponentProps = Readonly<{
    context: ViewContext,
    children?: React.ReactNode
}>;
type ComponentState = Readonly<{}>;

/******************************/

export default
class Gdrp
extends React.Component<ComponentProps, ComponentState> {

    showSettings = (): void => {
        this.props.context.openSettings('analytics');
    }

    close = (): void => {
        const globalStore = this.props.context.stores.global;

        globalStore.actions.setGdrpShown(true);

        this.forceUpdate();
    }

    /******************************/

    render(): React.ReactNode {
        let globalStore = this.props.context.stores.global,
            state = globalStore.getState();

        if (state.analytics.gdrpShown) {
            return null;
        }

        return (
            <div
                className="modal modal-gdrp"
                style={{
                    display: 'block'
                }}
            >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-body">
                            <p>
                                It's look like you are from the UE territory.<br />
                                So, I must have inform you about your's right to privacy settings choice.
                            </p>
                            <p>
                                The settings you can change here:&nbsp;
                                <button
                                    className="btn btn-primary"
                                    onClick={ this.showSettings }
                                    role="button"
                                    aria-pressed="false"
                                    aria-label="Settings"
                                >
                                    <FontAwesomeIcon icon="cog" />
                                </button>
                            </p>
                            <p>
                                In default, all options in the privacy settings are no limitet.
                            </p>
                        </div>
                        <div className="modal-footer">
                            <button
                                className="btn btn-primary"
                                onClick={ this.close }
                                role="button"
                                aria-pressed="false"
                                aria-label="Close"
                            >
                                OK
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
