import * as React from 'react';
import { History } from 'history';
import { IconBack } from 'view/components/icon';

/******************************/

type ComponentProps = Readonly<{
    history: History
    children?: React.ReactNode
}>;
export default
function BtnBackToHome(
    props: ComponentProps
): JSX.Element {
    return (
        <div className="btn-group">
            <button className="btn btn-primary ifx-ac mx-1" onClick={() => {
                props.history.push('/');
            }}>
                <IconBack />
            </button>
        </div>
    );
}