import * as React from 'react';
import { FontAwesomeIcon, FontAwesomeIconProps } from '@fortawesome/react-fontawesome';
import { library } from '@fortawesome/fontawesome-svg-core';

/******************************/

import {
    faPowerOff, faBars, faCrosshairs,
    faFlag, faUser, faAsterisk,
    faFire, faClock, faSignal,
    faCaretUp, faCaretDown, faShare, faCog,
    faSearch, faGlobe, faCubes, faHighlighter,
    faExpandArrowsAlt,
    faCube, faMedal, faFireAlt, faShieldAlt
} from '@fortawesome/free-solid-svg-icons';
library.add(
    faPowerOff, faBars, faCrosshairs,
    faFlag, faUser, faAsterisk,
    faFire, faClock, faSignal, faCog,
    faCaretUp, faCaretDown, faShare, faHighlighter,
    faSearch, faGlobe, faCubes, faExpandArrowsAlt,
    faCube, faMedal, faFireAlt, faShieldAlt
);

import {
    faDiscord, faCcPaypal, faFortAwesome
} from '@fortawesome/free-brands-svg-icons';
library.add(
    faCcPaypal, faDiscord, faFortAwesome
);

/******************************/

// content

const $faFortAwesome = ["fab", "fort-awesome"] as FontAwesomeIconProps['icon'];
export function IconVillage(): JSX.Element {
    return <FontAwesomeIcon icon={ $faFortAwesome } />;
}
export function IconCharacter(): JSX.Element {
    return <FontAwesomeIcon icon="user" />;
}
export function IconTribe(): JSX.Element {
    return <FontAwesomeIcon icon="asterisk" />;
}
export function IconRank(): JSX.Element {
    return <FontAwesomeIcon icon="cubes" />;
}
export function IconPoints(): JSX.Element {
    return <FontAwesomeIcon icon="cube" />;
}
export function IconPointsBash(): JSX.Element {
    return <FontAwesomeIcon icon="medal" />;
}
export function IconPointsOff(): JSX.Element {
    return <FontAwesomeIcon icon="fire-alt" />;
}
export function IconPointsDef(): JSX.Element {
    return <FontAwesomeIcon icon="shield-alt" />;
}

// gui

export function IconBack(): JSX.Element {
    return <FontAwesomeIcon icon="share" />;
}
export function IconGoTo(): JSX.Element {
    return <FontAwesomeIcon icon="crosshairs" />;
}
export function IconOnOff(): JSX.Element {
    return <FontAwesomeIcon icon="power-off" />;
}
export function IconMenu(): JSX.Element {
    return <FontAwesomeIcon icon="bars" />;
}
export function IconTop(): JSX.Element {
    return <FontAwesomeIcon icon="cubes" />;
}
export function IconWorld(): JSX.Element {
    return <FontAwesomeIcon icon="globe" />;
}
export function IconSettings(): JSX.Element {
    return <FontAwesomeIcon icon="cog" />;
}
export function IconFullscreen(): JSX.Element {
    return <FontAwesomeIcon icon="expand-arrows-alt" />;
}
export function IconSearch(): JSX.Element {
    return <FontAwesomeIcon icon="search" />;
}
export function IconMarker(): JSX.Element {
    return <FontAwesomeIcon icon="highlighter" />;
}

export function IconSpoilerHide(): JSX.Element {
    return <FontAwesomeIcon icon="caret-up" />;
}
export function IconSpoilerShow(): JSX.Element {
    return <FontAwesomeIcon icon="caret-down" />;
}

// brand

export function IconDiscord(): JSX.Element {
    return <FontAwesomeIcon icon={["fab", "discord"]} />
}
export function IconPayPal(): JSX.Element {
    return <FontAwesomeIcon icon={["fab", "cc-paypal"]} />
}