import * as React from 'react';
import ResultItem from 'view/components/search/ResultItem';
import { IconCharacter } from 'view/components/icon';

/******************************/

export default class Character extends ResultItem {
    getIcon(): JSX.Element {
        return (
            <IconCharacter />
        );
    }

    getName(): JSX.Element {
        return (
            <span className="name value-3">
                { this.props.item.name }
            </span>
        );
    }
}
