import * as React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import ResultItem from 'view/components/search/ResultItem';

/******************************/

export default class Character extends ResultItem {
    getIcon(): JSX.Element {
        return (
            <FontAwesomeIcon icon="user" />
        );
    }

    getName(): JSX.Element {
        return (
            <span className="name value-3">
                { this.props.item.name }
            </span>
        );
    }
}
