import * as React from 'react';
import ResultItem from 'view/components/search/ResultItem';
import { IconTribe } from 'view/components/icon';

/******************************/

export default class Tribe extends ResultItem {
    getIcon(): JSX.Element {
        return (
            <IconTribe />
        );
    }

    getName(): JSX.Element {
        return (
            <span className="name value-1">
                { this.props.item.name }
            </span>
        );
    }
}
