import * as React from 'react';
import ResultItem from 'view/components/search/ResultItem';
import GoToBtn from 'view/components/GoToBtn';
import Village from 'data/data/Village';
import { IconVillage } from 'view/components/icon';

/******************************/

export default
class VillageResult
extends ResultItem {
    getIcon(): JSX.Element {
        return (
            <IconVillage />
        );
    }

    getName(): JSX.Element {
        return (
            <span className="name value-2">
                { this.props.item.name }
            </span>
        );
    }

    getButtons(): JSX.Element {
        if (this.props.item instanceof Village) {
            return (
                <div className="ml-3">
                    <GoToBtn
                        village={ this.props.item }
                        context={ this.props.context }
                    />
                </div>
            );
        }

        return null;
    }
}
