import * as React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import ResultItem from 'view/components/search/ResultItem';
import GoToBtn from 'view/components/GoToBtn';
import Village from 'map/data/MapData/Village';

/******************************/

export default
class VillageResult
extends ResultItem {
    getIcon(): JSX.Element {
        return (
            <FontAwesomeIcon icon="flag" />
        );
    }

    getName(): JSX.Element {
        return (
            <span className="name value-2">
                { this.props.item.name }
            </span>
        );
    }

    getButtons(): JSX.Element {
        if (this.props.item instanceof Village) {
            return (
                <div className="ml-3">
                    <GoToBtn
                        village={ this.props.item }
                        map={ this.props.map }
                    />
                </div>
            );
        }

        return null;
    }
}
