import * as React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

/******************************/

const doc = window.document;
const docEl = doc.documentElement;

function toggleFullScreen(): void {
    let requestFullScreen = docEl.requestFullscreen;
    let cancelFullScreen = doc.exitFullscreen;

    if (!doc.fullscreenElement) {
        requestFullScreen.call(docEl);
    } else {
        cancelFullScreen.call(doc);
    }
}

/******************************/

type ComponentProps = Readonly<{
    children?: React.ReactNode
}>;
export default function BtnFullScreen(props: ComponentProps) {
    return (
        <button
            className="btn btn-primary ifx-ac mx-1"
            onClick={ toggleFullScreen }
        >
            <FontAwesomeIcon icon="expand-arrows-alt" />
        </button>
    );
}