import * as React from 'react';
import ModalManager, { Modal, Body, Hook } from "libs/ModalManager";
import { CanvasApi } from 'canvas';
import MenuItem from 'view/components/MenuItem';

/******************************/

export default function featureMenuItem(
    modalManager: ModalManager,
    mainMap: CanvasApi
): void {
    let hook: Hook = modalManager.createHook();

    hook.setLayout(
        <Modal
            hidden={ true }
            hideNotUnmount={ true }
            hideWhenInactive={ true }
            spyCursor={ false }
            canBeActive={ false }
            className="tooltipModal"
        >
            <Body className="p-0">
                <MenuItem hook={ hook } map={ mainMap } />
            </Body>
        </Modal>
    );

    window['mMenu'] = hook;
}