import * as React from 'react';
import { Modal, Body, Hook } from "libs/ModalManager";
import MenuItem from 'view/components/MenuItem';
import { ViewContext } from 'view/viewContext';

/******************************/

export default function featureMenuItem(
    context: ViewContext
): void {
    let hook: Hook = context.modalSystem.createHook();

    hook.setLayout(
        <Modal
            hidden={ true }
            hideNotUnmount={ true }
            hideWhenInactive={ true }
            spyCursor={ false }
            canBeActive={ false }
            className="tooltipModal"
        >
            <Body className="p-0">
                <MenuItem hook={ hook } context={ context } />
            </Body>
        </Modal>
    );

    window['mMenu'] = hook;
}