import * as React from 'react';
import { Modal, Header, Body, Footer, Hook } from "libs/ModalManager";
import { ViewContext } from 'view/viewContext';
import LazyPromise from 'libs/LazyPromise';
import Search from 'view/components/search/Search';

/******************************/

export default function featureSearch(
    context: ViewContext
): (tab?: string) => Promise<string> {
    let hook: Hook = context.modalSystem.createHook(),
        lazyPromise = new LazyPromise();

    hook.setLayout(
        <Modal
            hidden={ true }
            hideNotUnmount={ true }
            className="searchModal"
        >
            <Header>
                Search
            </Header>
            <Body className="p-2">
                <Search
                    hook={ hook }
                    context={ context }
                    search={ context.data.commands.search }
                    updateModal={ hook.update }
                />
            </Body>
        </Modal>
    );

    function open(): Promise<string> {
        if (hook.isHidden()) {
            hook.show();
            hook.setCursorPosition();
        }

        return lazyPromise.get();
    }

    window['mSearch'] = hook;

    return open;
}