import * as React from 'react';
import { Modal, Header, Body, Footer, Hook } from "libs/ModalManager";
import LazyPromise from 'libs/LazyPromise';

import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import MapColors from 'view/components/settings/MapColors';

import { ViewContext } from 'view/viewContext';

/******************************/

export default function featureSettings(
    context: ViewContext
): (tab?: string) => Promise<string> {
    let hook: Hook = context.modalSystem.createHook(),
        lazyPromise = new LazyPromise();

    hook.setLayout(
        <Modal
            hidden={ true }
            hideNotUnmount={ true }
        >
            <Header>
                Settings
            </Header>
            <Body
                className="p-2"
                style={{ minWidth: '20em' }}
            >
                <Tabs>
                    <TabList>
                        <Tab>World</Tab>
                        <Tab>Global</Tab>
                    </TabList>

                    <TabPanel>
                        <Tabs>
                            <TabList>
                                <Tab>Map colors</Tab>
                            </TabList>

                            <TabPanel>
                                <MapColors
                                    allowDisable={ true }
                                    context={ context }
                                    store={ context.stores.world }
                                />
                            </TabPanel>
                        </Tabs>
                    </TabPanel>

                    <TabPanel>
                        <Tabs>
                            <TabList>
                                <Tab>Map colors</Tab>
                            </TabList>

                            <TabPanel>
                                <MapColors
                                    context={ context }
                                    store={ context.stores.global }
                                />
                            </TabPanel>
                        </Tabs>
                    </TabPanel>
                </Tabs>
            </Body>
        </Modal>
    );

    function open(
        tab?: string
    ): Promise<string> {
        if (hook.isHidden()) {
            hook.show();
            hook.setCursorPosition();
        }

        return lazyPromise.get();
    }

    window['mSettings'] = hook;

    return open;
}