import * as React from 'react';
import ModalManager, { Modal, Body, Hook } from "libs/ModalManager";
import { CanvasApi } from 'canvas';
import Tooltip from 'view/components/Tooltip';

/******************************/

export default function featureTooltip(
    modalManager: ModalManager,
    mainMap: CanvasApi
): void {
    let hook: Hook = modalManager.createHook();

    hook.setLayout(
        <Modal
            hidden={ true }
            hideNotUnmount={ true }
            hideWhenInactive={ true }
            spyCursor={ true }
            canBeActive={ false }
            className="tooltipModal"
        >
            <Body className="p-0">
                <Tooltip
                    hook={ hook }
                    map={ mainMap }
                />
            </Body>
        </Modal>
    );

    window['mTooltip'] = hook;
}