import * as React from 'react';

/******************************/

const MARKET_NAME = {
    'beta': 'Beta',
    'en': 'United Kingdom',
    'us': 'USA',
    'de': 'Germany',
    'nl': 'Netherlands',
    'fr': 'France',
    'pl': 'Poland',
    'ru': 'Russia',
    'br': 'Brazil',
    'es': 'Spain',
    'it': 'Italy',
    'gr': 'Greece',
    'tr': 'Turkey',
    'cz': 'Czech Republic',
    'pt': 'Portugal',
    'se': 'Sweden',
    'no': 'Norway',
    'dk': 'Denmark',
    'fi': 'Finland',
    'sk': 'Slovakia',
    'ro': 'Romania',
    'hu': 'Hungary'
};

/******************************/

type ComponentProps = Readonly<{
    children?: React.ReactNode
}>;
export default
function Market(
    props: {
        marketId: string,
        onClick?: (event: React.MouseEvent) => void
    } & ComponentProps
): JSX.Element {
    let market = props.marketId;

    return (
        <button
            className="btn btn-dark m-1"
            onClick={ props.onClick || null }
        >
            <img
                className="img-flag"
                src={ `/flags/${ market }.svg` }
                alt={ MARKET_NAME[ market ] || market }
            />
            &nbsp;
            { MARKET_NAME[ market ] || market }
        </button>
    );
}