import * as React from 'react';

/******************************/

type ComponentProps = Readonly<{
    children?: React.ReactNode
}>;
export default
function World(
    props: {
        worldId: string,
        worldName?: string,
        active?: boolean,
        onClick?: (event: React.MouseEvent) => void
    } & ComponentProps
): JSX.Element {
    let worldId = props.worldId,
        worldName = props.worldName,
        isActive = props.active === undefined ? true : props.active;

    if (props.onClick || !isActive) {
        return (
            <button
                className="btn btn-dark m-1"
                onClick={ props.onClick }
            >
                <b>{ worldId }</b> <em>{ worldName }</em>
            </button>
        );
    } else {
        return (
            <span
                className="btn-outline-dark btn-light m-1"
            >
                <b>{ worldId }</b> <em>{ worldName }</em>
            </span>
        );
    }
}