import { Context } from "context";
import ModalManager from "libs/ModalManager/ModalManager";

/******************************/

export class ViewContext extends Context {
    modalSystem: ModalManager;
}

/******************************/

export default function createViewContext(
    context: Context
) {
    Object.setPrototypeOf(context, ViewContext.prototype);

    context['modalSystem'] = new ModalManager();

    return context as ViewContext;
}